package Hackerearth;
import java.util.*;

public class RoboticMoves {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner s=new Scanner(System.in);

		int t=s.nextInt();

		while(t-->0)

		{

		int n=s.nextInt();

		int move=n*(n+1);

		System.out.println(move);

		}

	}

}
