package stringprograms;
import java.util.*;
public class Multipleofthree {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc= new Scanner(System.in);
		int t=sc.nextInt();
        while(t>0){
        int n=sc.nextInt();
        int result=3*n;
        System.out.println(result);
        }
        t--;

	}

}
